mapper.select:
select  from t_hr_regular entity

     and entity.title like concat('%', #{title}, '%')
     and entity.reason = #{reason}
     and entity.regular_time = #{regularTime}

        and ( entity.dept_id = #{deptId} or entity.dept_id
        IN (SELECT dept_id FROM sys_dept WHERE ancestors like CONCAT(CONCAT_WS(',',(SELECT ancestors FROM sys_dept
        WHERE dept_id = #{deptId} and STATUS = '0' and del_flag = '0'),#{deptId}),'%') and STATUS = '0' and del_flag = '0'))
     and entity.dept_name like concat('%', #{deptName}, '%')
     and entity.post_id = #{postId}
     and entity.post_name like concat('%', #{postName}, '%')
     and entity.instance_id = #{instanceId}
     and entity.apply_user = #{applyUser}
     and date_format(entity.apply_time,'%Y-%m-%d') = DATE_FORMAT(#{applyTime}, '%Y-%m-%d')
     and entity.instance_id = #{instanceId}
     and entity.create_by = #{createBy}
     and entity.audit_status = #{auditStatus}
     and entity.emp_id = #{empId}
     and entity.apply_user_name = #{applyUserName}
    and entity.del_flag = 0

select  from t_hr_regular entity
LEFT JOIN t_hr_emp e ON entity.emp_id = e.emp_id
LEFT JOIN sys_dept d ON e.dept_id = d.dept_id

     and entity.title like concat('%', #{title}, '%')
     and entity.reason = #{reason}
     and entity.regular_time = #{regularTime}

        and ( entity.dept_id = #{deptId} or entity.dept_id
        IN (SELECT dept_id FROM sys_dept WHERE ancestors like CONCAT(CONCAT_WS(',',(SELECT ancestors FROM sys_dept
        WHERE dept_id = #{deptId} and STATUS = '0' and del_flag = '0'),#{deptId}),'%') and STATUS = '0' and del_flag = '0'))
     and entity.dept_name like concat('%', #{deptName}, '%')
     and entity.post_id = #{postId}
     and entity.post_name like concat('%', #{postName}, '%')
     and entity.instance_id = #{instanceId}
     and entity.apply_user = #{applyUser}
     and date_format(entity.apply_time,'%Y-%m-%d') = DATE_FORMAT(#{applyTime}, '%Y-%m-%d')
     and entity.instance_id = #{instanceId}
     and entity.create_by = #{createBy}
     and entity.audit_status = #{auditStatus}
     and entity.emp_id = #{empId}
     and entity.apply_user_name = #{applyUserName}
    and entity.del_flag = 0

    ${params.dataScope}
